import React, { Component } from 'react'
import axios from 'axios'

const CommentItem = (props) => {
    return(
        <div>
            Comments:
            <div>{props.text}</div>
            <div>{props.time}</div>
            <div>{props.author}</div>
        </div>
    )
}

class View extends Component {
    constructor() {
        super();
        this.state = {
            commentText: "",
            title: "",
            description: "",
            author: "",
            time: "",
            comments: [{
                text: "",
                time: "",
                author: ""
            }]
        }
        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleChange = e => {
        console.log(e.target.name);
        console.log(e.target.name);

        this.setState({[e.target.name] : e.target.value})
    }

    handleSubmit = e => {
        e.preventDefault();
        const comment = {
            text: this.state.commentText,
            username: this.props.username
        }
        console.log(comment);

        axios.post('http://localhost:5000/blogs/'+this.props.match.params.id, null, {params: comment})
        .then(res => {
            // console.log(res);
            console.log(res.data);
        })
    }

    componentDidMount() {
        console.log(this.props);
        axios.get('http://localhost:5000/blogs/'+this.props.match.params.id)
      .then(res => {
        const data = res.data;
        console.log(data);
        const comments = data.comments.map((com, ind) => {
            return {
                text: com.text,
                time: com.createdAt,
                author: com.author.username
            }
        })
        const blog = {
            title: data.title,
            description: data.description,
            author: data.author.username,
            time: data.createdAt,
            _id: data._id
        }
        this.setState({...blog, comments})
      })
    }

    commentText
    render() {
        const comments = this.state.comments.map((com, index) => (
            <CommentItem key={index} text={com.text} time={com.time} author={com.author}/>
            ))
        const {commentText} = this.state;
        // console.log(commentText);
        return(
            <div>
                <div>{this.state.title}</div>
                <div>{this.state.description}</div>
                <div>{this.state.author}</div>
                <div>{this.state.time}</div>
                {comments}
                <form onSubmit={this.handleSubmit}>
                    <input id="text" key="text" type="text" name="text" value={commentText} onChange={this.handleChange} required/>
                    <button>Add Comment</button>
                </form>
            </div>
        )
    }
}

export default View;
