import React, { Component } from 'react';
// import logo from './logo.svg';
import './App.css';
import axios from 'axios'

class App extends Component {
  constructor(props) {

  }
  state = {
    data: '',
    blog: {
      title: '',
      description: ''
    }
  }

  handleChange = e => {
    // e.preventDefault();
    // console.log("Target : "+ e.target.title);
    this.setState({[e.target.name] : e.target.value})
  }

  handleSubmit = e => {
    e.preventDefault();
    // const blog = {
    //   blog: { 
    //     title: this.state.blog.title,
    //     description: this.state.blog.description,
    //   }
    // }
    console.log(this.state.blog.title);
    const blog = {
      title: this.state.blog.title,
      description: this.state.blog.description
    }
    console.log(blog);

    axios.post('http://localhost:5000/', null, {params: {blog}})
    .then(res => {
      console.log(res);
      console.log(res.data);
    })
  }

  componentDidMount() {
    // const data = '';
    axios.get('http://localhost:5000/')
    .then(res => {
      const data = res.data;
      const Hi = data['Hi']
      // console.log(Hi);
      this.setState({data:Hi})
    })


  }

  render() {
    const {title, description} = this.state.blog;
    console.log(title);
    return(
      <div>
        Hello World
        {this.state.data}
        <form onSubmit={this.handleSubmit}>
            <input type="text" name="title" value={title} onChange={this.handleChange}/>
            <input type="text" name="description" value={description} onChange={this.handleChange}/>
            <button type="submit">Submit</button>
        </form>
      </div>
    )
  }
}

