import React, { Component } from 'react'
import axios from 'axios'

class View extends Component {
    constructor() {
        super();
        this.state = {
            title: "",
            description: "",
            author: "",
            time: "",
            comments: {
                text: "",
                time: ""
            }
        }
    }

    componentDidMount() {
        axios.get('http://localhost:5000/blogs/'+this.props.match.params)
      .then(res => {
        const data = res.data;
        console.log(data);
        const blogs = data.map((blg, ind) => {
          return {
            title: blg.title,
            description: blg.description,
            author: blg.author.username,
            time: blg.createdAt,
            _id: blg._id,
            comments: {
                text: blg.comments.text,
                time: blg.comments.createdAt
            }
          }
        })
        this.setState({blogs: blogs})
      })
    }


    render() {
        // const blogs = this.state.blogs.map((blg, index) => (
        //     <ViewItem key={index} title={blg.title} description={blg.description} author={blg.author} time={blg.time} _id={blg._id}/>
        //     ))
        return(
            <div>{blogs.title}</div>
        )
    }
}

export default View;
