import React, { Component } from 'react'
import axios from 'axios'

const CommentItem = (props) => {
    return(
        <div>
            <div>{this.props.text}</div>
            <div>{this.props.time}</div>
            <div>this.props.author</div>
        </div>
    )
}

class View extends Component {
    constructor() {
        super();
        this.state = {
            title: "",
            description: "",
            author: "",
            time: "",
            comments: [{
                text: "",
                time: "",
                author: ""
            }]
        }
    }

    componentDidMount() {
        console.log(this.props);
        axios.get('http://localhost:5000/blogs/'+this.props.match.params.id)
      .then(res => {
        const data = res.data;
        console.log(data);
        const comments = data.comments.map((com, ind) => {
            return {
                text: com.text,
                time: com.createdAt,
                author: com.author.username
            }
        })
        const blog = {
            title: data.title,
            description: data.description,
            author: data.author.username,
            time: data.createdAt,
            _id: data._id
            // comments: {
            //     text: data.comments[0].text,
            //     time: data.comments[0].createdAt,
            //     author: data.comments[0].author.username
            // }
        }
        // const blogs = data.map((blg, ind) => {
        //   return {
        //     title: blg.title,
        //     description: blg.description,
        //     author: blg.author.username,
        //     time: blg.createdAt,
        //     _id: blg._id,
        //     comments: {
        //         text: blg.comments.text,
        //         time: blg.comments.createdAt
        //     }
        //   }
        // })
        this.setState({...blog, comments})
      })
    }


    render() {
        const comments = this.state.comments.map((com, index) => (
            <CommentItem key={index} text={com.text} time={com.time} author={com.author}/>
            ))
        return(
            <div>

                <div>{this.state.title}</div>
                <div>{this.state.description}</div>
                <div>{this.state.author}</div>
                <div>{this.state.time}</div>
                {comments}
                {/* <div>{this.state.comments.text}</div>
                <div>{this.state.comments.time}</div>
                <div>{this.state.comments.author}</div> */}

            </div>
        )
    }
}

export default View;
