import React, { Component } from 'react'
import axios from 'axios'

class View extends Component {
    constructor() {
        super();
        this.state = {
            title: "",
            description: "",
            author: "",
            time: "",
            comments: [{
                text: "",
                time: "",
                author: ""
            }]
        }
    }

    componentDidMount() {
        console.log(this.props);
        axios.get('http://localhost:5000/blogs/'+this.props.match.params.id)
      .then(res => {
        const data = res.data;
        console.log(data);
        const comments = data.comments.map((com, ind) => {
            return {
                text: com.text,
                time: com.createdAt,
                author: com.author.username
            }
        })
        const blog = {
            title: data.title,
            description: data.description,
            author: data.author.username,
            time: data.createdAt,
            _id: data._id
            // comments: {
            //     text: data.comments[0].text,
            //     time: data.comments[0].createdAt,
            //     author: data.comments[0].author.username
            // }
        }
        // const blogs = data.map((blg, ind) => {
        //   return {
        //     title: blg.title,
        //     description: blg.description,
        //     author: blg.author.username,
        //     time: blg.createdAt,
        //     _id: blg._id,
        //     comments: {
        //         text: blg.comments.text,
        //         time: blg.comments.createdAt
        //     }
        //   }
        // })
        this.setState({...blog})
      })
    }


    render() {
        // const blogs = this.state.blogs.map((blg, index) => (
        //     <ViewItem key={index} title={blg.title} description={blg.description} author={blg.author} time={blg.time} _id={blg._id}/>
        //     ))
        return(
            <div>

                <div>{this.state.blogs.title}</div>
                <div>{this.state.blogs.description}</div>
                <div>{this.state.blogs.author}</div>
                <div>{this.state.blogs.time}</div>
                <div>{this.state.blogs.comments.text}</div>
                <div>{this.state.blogs.comments.time}</div>
                <div>{this.state.blogs.comments.author}</div>

            </div>
        )
    }
}

export default View;
