class App extends Component {
  state = {
    data: '',
    blog: {
      title: '',

    }
  }

  componentDidMount() {
    // const data = '';
    axios.get('http://localhost:5000/')
    .then(res => {
      const data = res.data;
      const Hi = data['Hi']
      // console.log(Hi);
      this.setState({data:Hi})
    })

    const blog = {
      title: this.state.blog.title,
      description: this.state.blog.description
    }
    axios.post('http://localhost:5000/', {blog})
    .then(res => {
      console.log(res);
      console.log(res.data);
    })
  }

  render() {
    return(
      <div>
        Hello World
        {this.state.data}
      </div>
    )
  }
}

