import React, { Component } from 'react'
import axios from 'axios'

class Login extends Component {
    constructor(props) {
        super(props);
        this.state =  {
            username : "",
            password : "",
            // isloggedin : ""
        }
        // console.log(props);
        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
        // this.props.updateUser = this.props.updateUser.bind(this);
    }

    handleChange = e => {
        this.setState({[e.target.name] : e.target.value})
    }

    handleSubmit = e => {
        e.preventDefault();
        const user = {
            // name: this.state.name,
            username: this.state.username,
            password: this.state.password
        }
        console.log(user);

    axios.post('http://localhost:5000/login', null, {
        params: user,
        headers: {
            'Access-Control-Allow-Origin': '*',
        }
        })
    .then(res => {
            if(res.status == 200)
            {
                console.log("Logged In");
                // this.setState({isloggedin: "true"});
                this.props.updateUser({
                    loggedIn: true,
                    username: res.data.username
                })
            }
            else
            {
                console.log("Failed to log in");
            }
            // console.log("STATUS : " + res.status);
            // console.log(res);
            console.log(res.data);
        })
    }

    render() {
        const {username, password, isloggedin} = this.state;
        console.log(isloggedin);
        if(isloggedin=="true"){
            return(
                <div>Hello</div>
            )
        }
        else {
            return (
                <form onSubmit={this.handleSubmit}>
                <label>Email
                    <input key="username" type="email" name="username" value={username} onChange={this.handleChange}/>
                </label>
                <label>Password
                    <input key="password" type="password" name="password" value={password} onChange={this.handleChange}/>
                </label>
                <button type="submit">Log In</button>
            </form>
            )
        }
    }
}
export default Login;
