import React, { Component } from 'react'
import axios from 'axios'

class View extends Component {
    constructor() {
        super();
        this.state = {
            blogs: {

                title: "",
                description: "",
                author: "",
                time: "",
                comments: {
                    text: "",
                    time: ""
                }
            }
        }
    }

    componentDidMount() {
        console.log(this.props);
        axios.get('http://localhost:5000/blogs/'+this.props.match.params.id)
      .then(res => {
        const data = res.data;
        console.log(data);
        const blog = {
            title: data.title,
            description: data.description,
            author: data.author.username,
            time: data.createdAt,
            _id: data._id,
            comments: {
                text: data.comments.text,
                time: data.comments.createdAt
            }
        }
        // const blogs = data.map((blg, ind) => {
